import React,{Component} from 'react';
import {Modal,Button,Row,Col,Form, FormGroup} from'react-bootstrap';
import Snackbar from '@material-ui/core/Snackbar';
import MuiAlert from '@material-ui/lab/Alert';
import iconButton from '@material-ui/core/IconButton';

function Alert(props) {
  return <MuiAlert elevation={6} variant="filled" {...props} />;
}
export class AppDepModel extends Component{
 constructor(props){
   super(props);

   this.state={snackbaropen:false,snackbarmsg:""};
   this.handleSubmit=this.handleSubmit.bind(this);
  
 }
 snackbarClose(){
   this.setState({snackbaropen:false});
 }
 handleSubmit(event){
   event.preventDefault();

   fetch('https://localhost:44301/api/department',{
     method:'POST',
     headers:{
       'Accept':'application/json',
       'Content-Type':'application/json'
     },
     body:JSON.stringify({
       DepartmentId:"null",
       DepartmentName:event.target.DepartmentName.value
     })
   })
   .then(res=> res.json()) //converting response to json
   .then((result)=>{
     alert(result); //whatever the result come is comes in alert
   },
   (error)=>{
     alert('failed') //if result doesnt appear then failed message appears
   }
   )
 }


    render()
    {
        return(

          //(' yesto open bracket same line ma hunu purxa natra error(Expected an assignment or function call instead saw an Exception aauxa)
          <div className="container">
            <Snackbar
            anchorOrigin={{vertical:'center',horizental:'center'}}
            open={this.state.snackbaropen} //it will open based on the value inside curly braces
            autoHideDuration={3000}
            onClose={this.snackbarClose}>
               <Alert onClose={this.state.snackbaropen} severity="success">
               message={<span id='mesage-id'>{this.setState.snackbarmsg}</span>}
  </Alert>
            </Snackbar>


            <Modal
            {...this.props}//kaha ko props define vako xoina vane props lai this.props garnu purxa
            size="lg"
            aria-labelledby="contained-modal-title-vcenter"
            centered
          >
            <Modal.Header closeButton>
              <Modal.Title id="contained-modal-title-vcenter">
                Add Department
              </Modal.Title>
            </Modal.Header>
            <Modal.Body>
          <Row>
            <Col sm={6}>
              <Form onSubmit={this.handleSubmit}>
                <Form.Group controlId="DepartmentName">
                  <Form.Label>DepartmentName</Form.Label>
                  <Form.Control
                   type="text" 
                   name="DepartmentName"
                    required
                     placeholder="DepartmentName"/>
                  

                </Form.Group>
                <FormGroup>
                  <Button variant="primary" type="submit">Add Department </Button>
                </FormGroup>
              </Form>
            </Col>
          </Row>
            </Modal.Body>
            <Modal.Footer>
              <Button variant="danger" onClick={this.props.onHide}>Close</Button>
            </Modal.Footer>
          </Modal>
          </div>

        );
    }
}
