import React,{Component} from 'react';
import {Table, Dropdown} from 'react-bootstrap';
//Component can be anything it can be model pop_up check_box or the pages it has multiple application
export class Department extends Component{//Basically we are trying to Create class Home as components 

    constructor(props){
        super(props);
        this.state={deps:[]}//deps object is created !! //to initialize (this) keyword we have to Write super
    }
    refreshList(){
        this.setState({
            deps:[{"DepartmentId":1,"DepartmentName":"IT"},
            {"DepartmentId":2,"DepartmentName":"management"}
        ]

        })
    }

    render(){                                             //this is basically whats need to be rendered when the component is called or initiated
           const {deps}=this.state;
        return(
               <Table className="mt-4" stripped bordered hover size="sm">
               <thead>
                   <th>DepartmentId</th>
                   <th>DepartmentName</th>
               </thead>
               <tbody>
                {deps.map(dep=>
                    <tr>key={dep.DepartmentId}
                    <td>{dep.DepartmentId}</td>
                    <td>{dep.DepartmentName}</td>
                    </tr>
                    )}
               </tbody>
               </Table>
            )
    } 
}
