import React,{Component} from 'react';
import {Table, Dropdown} from 'react-bootstrap';
import {Button,ButtonToolBar} from 'react-bootstrap';
import {AppDevModal} from './AddDepModel';
//Component can be anything it can be model pop_up check_box or the pages it has multiple application
export class Department extends Component{//Basically we are trying to Create class Department as components 

    constructor(props){
        super(props);
        this.state={deps:[],addModelShow :false}//deps object is created !! //to initialize (this) keyword we have to Write super
    }
    componentDidMount(){ //here the componentDidMount vanyo vane yo compoonet load huda component render hunxa 
        this.refreshList();
    }

    refreshList(){
        fetch('https://localhost:44394/api/department')
        .then(response=> response.json()) //convert response into json data
        .then(data =>{
            this.setState({deps:data});
        });
       /* this.setState({
            deps:[{"DepartmentId":1,"DepartmentName":"IT"},
            {"DepartmentId":2,"DepartmentName":"management"}// this is Dummy Data for test
        ]

        })*/
    }

    render(){                                             //this is basically whats need to be rendered when the component is called or initiated
           const {deps}=this.state;//here we have to again declare (object) from the (state)
           let addModelClose =() =>this.setState({addModelShow:false})

        return(
            <div>
               <Table className="mt-4" striped bordered hover size="sm">
               <thead>
                   <tr>
                   <th>DepartmentId</th>
                   <th>DepartmentName</th>
                   </tr>
               </thead>
               <tbody>
                {deps.map(dep=>
                    <tr key={dep.DepartmentId}>
                    <td>{dep.DepartmentId}</td>
                    <td>{dep.DepartmentName}</td>
                    </tr>
                    )}
               </tbody>
               </Table>
               <ButtonToolBar>
                   <Button variant="primary"
                    onClick={()=>this.setState({addModelShow:true})}>
                            Add Department
                   </Button>
                   <AppDevModal show={this.state.addModelShow}
                   onHide={addModelClose}
                   />
               </ButtonToolBar>
               </div>
            )
    } 
}
