import React,{Component} from 'react';
import {Modal,Button,Row,Col,Form, FormGroup} from'react-bootstrap';

export class AppDepModel extends Component{
 constructor(props){
   super(props);
  
 }
 handleSubmit(event){
   event.preventDefault();

   alert(event.target.DepartmentName.value);
 }

    render()
    {
        return(
          //'(' yesto open bracket same line ma hunu purxa natra error(Expected an assignment or function call instead saw an Exception aauxa)
            <Modal
            {...this.props}//kaha ko props define vako xoina vane props lai this.props garnu purxa
            size="lg"
            aria-labelledby="contained-modal-title-vcenter"
            centered
          >
            <Modal.Header closeButton>
              <Modal.Title id="contained-modal-title-vcenter">
                Add Department
              </Modal.Title>
            </Modal.Header>
            <Modal.Body>
          <div className="container">
          <Row>
            <Col sm={6}>
              <Form onSubmit={this.handleSubmit}>
                <Form.Group controlId="DepartmentName">
                  <Form.Label>DepartmentName</Form.Label>
                  <Form.Control
                   type="text" 
                   name="DepartmentName"
                    required
                     placeholder="DepartmentName"/>
                  

                </Form.Group>
                <FormGroup>
                  <Button variant="primary" type="submit">Add Department </Button>
                </FormGroup>
              </Form>
            </Col>
          </Row>
          </div>
            </Modal.Body>
            <Modal.Footer>
              <Button variant="danger" onClick={this.props.onHide}>Close</Button>
            </Modal.Footer>
          </Modal>
        );
    }
}
